import React, { useState } from 'react';
import axios from 'axios';
import { RingLoader } from 'react-spinners'; // Make sure to install react-spinners

function App() {
  // State variables for the form inputs
  const [insertKey, setInsertKey] = useState('');
  const [insertValue, setInsertValue] = useState('');
  const [deleteKey, setDeleteKey] = useState('');
  const [searchKey, setSearchKey] = useState('');
  const [searchResult, setSearchResult] = useState(null);
  const [notification, setNotification] = useState('');
  const [loading, setLoading] = useState(false);
  const [notificationType, setNotificationType] = useState('success'); // New state variable for notification type
  const [selectedHashing, setSelectedHashing] = useState('extendible');
  const [extendibleIndex, setExtendibleIndex] = useState(null);
  const [linearIndex, setLinearIndex] = useState(null);

  // Handle insertion
  const handleInsert = async (e) => {
    e.preventDefault();
    if (!insertKey || !insertValue) {
      setNotification('Key and Value must be provided for insertion.');
      setNotificationType('error');
      return;
    }

    setLoading(true);
    try {
      const response = await axios.post(`http://127.0.0.1:5000/${selectedHashing}/insert`, {
        key: parseInt(insertKey),
        value: insertValue,
      });
      setNotification(response.data.message); // Show message from API response
      setNotificationType('success');
      setInsertKey('');
      setInsertValue('');
    } catch (error) {
      setNotification('Error inserting data: ' + error.response.data.error);
      setNotificationType('error');
    } finally {
      setLoading(false);
    }
  };

  // Handle deletion
  const handleDelete = async (e) => {
    e.preventDefault();
    if (!deleteKey) {
      setNotification('Key must be provided for deletion.');
      setNotificationType('error');
      return;
    }

    setLoading(true);
    try {
      const response = await axios.delete(`http://127.0.0.1:5000/${selectedHashing}/delete`, {
        data: { key: parseInt(deleteKey) },
      });
      setNotification(response.data.message); // Show message from API response
      setNotificationType('success');
      setDeleteKey('');
    } catch (error) {
      setNotification('Error deleting data: ' + error.response.data.error);
      setNotificationType('error');
    } finally {
      setLoading(false);
    }
  };

  // Handle search
  const handleSearch = async (e) => {
    e.preventDefault();
    if (!searchKey) {
      setNotification('Key must be provided for search.');
      setNotificationType('error');
      return;
    }

    setLoading(true);
    try {
      const response = await axios.get(`http://127.0.0.1:5000/${selectedHashing}/search?key=${searchKey}`);
      setSearchResult(response.data.message); // Show message from API response
      setNotification(`Found key ${searchKey}: ${response.data.message}`);
      setNotificationType('success');
      setSearchKey('');
    } catch (error) {
      setNotification('Error searching data: ' + error.response.data.error);
      setNotificationType('error');
    } finally {
      setLoading(false);
    }
  };

  // Get Extendible Index
  const getExtendibleIndex = async () => {
    setLoading(true);
    try {
      const response = await fetch('/extendible/index');
      const data = await response.json();
      setExtendibleIndex(data);
      setNotification('Extendible index retrieved successfully.');
      setNotificationType('success');
    } catch (error) {
      setNotification('Error retrieving extendible index: ' + error.message);
      setNotificationType('error');
    } finally {
      setLoading(false);
    }
  };

  // Get Linear Index
  const getLinearIndex = async () => {
    setLoading(true);
    try {
      const response = await fetch('/linear/index');
      const data = await response.json();
      setLinearIndex(data);
      setNotification('Linear index retrieved successfully.');
      setNotificationType('success');
    } catch (error) {
      setNotification('Error retrieving linear index: ' + error.message);
      setNotificationType('error');
    } finally {
      setLoading(false);
    }
  };

  // Reset all fields
  const handleReset = () => {
    setInsertKey('');
    setInsertValue('');
    setDeleteKey('');
    setSearchKey('');
    setSearchResult(null);
    setNotification('');
    setExtendibleIndex(null);
    setLinearIndex(null);
  };

  return (
    <div className="App">
      <h1>Hashing Application</h1>

      {/* Dropdown to select between Extendible and Linear Hashing */}
      <div>
        <label>Select Hashing Type: </label>
        <select
          value={selectedHashing}
          onChange={(e) => setSelectedHashing(e.target.value)}
        >
          <option value="extendible">Extendible Hashing</option>
          <option value="linear">Linear Hashing</option>
        </select>
      </div>

      {/* Insert Section */}
      <h2>Insert</h2>
      <form onSubmit={handleInsert}>
        <input
          type="number"
          placeholder="Key"
          value={insertKey}
          onChange={(e) => setInsertKey(e.target.value)}
        />
        <input
          type="text"
          placeholder="Value"
          value={insertValue}
          onChange={(e) => setInsertValue(e.target.value)}
        />
        <button type="submit" disabled={loading}>
          {loading ? 'Inserting...' : 'Insert'}
        </button>
      </form>

      {/* Delete Section */}
      <h2>Delete</h2>
      <form onSubmit={handleDelete}>
        <input
          type="number"
          placeholder="Key"
          value={deleteKey}
          onChange={(e) => setDeleteKey(e.target.value)}
        />
        <button type="submit" disabled={loading}>
          {loading ? 'Deleting...' : 'Delete'}
        </button>
      </form>

      {/* Search Section */}
      <h2>Search</h2>
      <form onSubmit={handleSearch}>
        <input
          type="number"
          placeholder="Key"
          value={searchKey}
          onChange={(e) => setSearchKey(e.target.value)}
        />
        <button type="submit" disabled={loading}>
          {loading ? 'Searching...' : 'Search'}
        </button>
      </form>

      {/* Get Index Section */}
      <h2>Get Index</h2>
      <button onClick={getExtendibleIndex} disabled={loading}>
        {loading ? 'Loading...' : 'Get Extendible Index'}
      </button>
      <button onClick={getLinearIndex} disabled={loading}>
        {loading ? 'Loading...' : 'Get Linear Index'}
      </button>

      {/* Notification Display */}
      {notification && (
        <div className={`notification ${notificationType}`}>
          {notification}
          <button onClick={() => setNotification('')}>Close</button>
        </div>
      )}

      {/* Reset Button */}
      <button onClick={handleReset} style={{ marginTop: '20px' }}>
        Reset All
      </button>

      {/* Search Result Display */}
      {searchResult && (
        <div className="search-result">
          <h2>Search Result</h2>
          <p>{searchResult}</p>
          <button onClick={() => setSearchResult(null)}>Clear</button>
        </div>
      )}

      {/* Extendible Index Display */}
      {extendibleIndex && (
        <div className="extendible-index">
          <h2>Extendible Index</h2>
          <pre>{JSON.stringify(extendibleIndex, null, 2)}</pre>
        </div>
      )}

      {/* Linear Index Display */}
      {linearIndex && (
        <div className="linear-index">
          <h2>Linear Index</h2>
          <pre>{JSON.stringify(linearIndex, null, 2)}</pre>
        </div>
      )}

      {/* Loading Spinner */}
      {loading && (
        <div style={{ display: 'flex', justifyContent: 'center', margin: '20px 0' }}>
          <RingLoader loading={loading} size={50} />
        </div>
      )}
    </div>
  );
}

export default App;
