import React, { useState } from 'react';
import axios from 'axios';
import { RingLoader } from 'react-spinners';
import './App.css';

function App() {
  const [insertKey, setInsertKey] = useState('');
  const [deleteKey, setDeleteKey] = useState('');
  const [searchKey, setSearchKey] = useState('');
  const [searchResult, setSearchResult] = useState(null);
  const [notification, setNotification] = useState([]);
  const [loading, setLoading] = useState(false);
  const [selectedHashing, setSelectedHashing] = useState('extendible');
  const [hashTableState, setHashTableState] = useState(null);

  // Function to handle inserting a new key
  const handleInsert = async (e) => {
    e.preventDefault();
    console.log("Insert Key:", insertKey); // Check the value of insertKey
    if (!insertKey) {
        addNotification('error', 'Key must be provided for insertion.');
        return;
    }

    setLoading(true);
    try {
      const response = await axios.post(`http://127.0.0.1:5000/${selectedHashing}/insert`, {
        key: parseInt(insertKey)
      });

      if (response.data && response.data.state) {
        addNotification('success', response.data.message);
        setHashTableState(response.data.state);
      } else {
        throw new Error("Invalid response structure");
      }

      setInsertKey(''); // Clear input field after successful insert
    } catch (error) {
      const errorMessage = error.response?.data?.error || 'Error inserting data: ' + error.message;
      addNotification('error', errorMessage);
    } finally {
      setLoading(false);
    }
  };

  // Function to handle deleting a key
  const handleDelete = async (e) => {
    e.preventDefault();
    if (!deleteKey) {
      addNotification('error', 'Key must be provided for deletion.');
      return;
    }

    setLoading(true);
    try {
      const response = await axios.delete(`http://127.0.0.1:5000/${selectedHashing}/delete`, {
        data: { key: parseInt(deleteKey, 10) } // Ensure key is parsed as an integer
      });

      if (response.data && response.data.state) {
        addNotification('success', response.data.message);
        setHashTableState(response.data.state);
      } else {
        throw new Error("Invalid response structure");
      }

      setDeleteKey(''); // Clear input field after successful delete
    } catch (error) {
      const errorMessage = error.response?.data?.error || 'Error deleting data: ' + error.message;
      addNotification('error', errorMessage);
    } finally {
      setLoading(false);
    }
  };

  // Function to handle searching a key
  const handleSearch = async (e) => {
    e.preventDefault();
    if (!searchKey) {
      addNotification('error', 'Key must be provided for search.');
      return;
    }

    setLoading(true);
    try {
      const response = await axios.get(`http://127.0.0.1:5000/${selectedHashing}/search`, {
        params: { key: parseInt(searchKey, 10) } // Ensure key is parsed as an integer
      });

      if (response.data && response.data.message) {
        setSearchResult(response.data.message);
        addNotification('success', `Found key ${searchKey}: ${response.data.message}`);
        setHashTableState(response.data.state);
      } else {
        throw new Error("Invalid response structure");
      }

      setSearchKey(''); // Clear input field after successful search
    } catch (error) {
      const errorMessage = error.response?.data?.error || 'Error searching data: ' + error.message;
      addNotification('error', errorMessage);
    } finally {
      setLoading(false);
    }
  };

  const addNotification = (type, message) => {
    const newNotification = { id: new Date().getTime(), type, message };
    setNotification((prev) => [...prev, newNotification]);
  };

  const handleReset = () => {
    setInsertKey('');
    setDeleteKey('');
    setSearchKey('');
    setSearchResult(null);
    setNotification([]);
    setHashTableState(null); // Reset hash table state
  };

  const renderHashTableState = () => {
    if (!hashTableState) return null;

    return (
      <div className="hash-table-state">
        <h2>Hash Table State</h2>
        <div><strong>Global Depth:</strong> {hashTableState.global_depth}</div>
        {Object.entries(hashTableState.buckets).map(([index, bucket]) => (
          <div key={index}>
            <strong>Bucket {index} (Local Depth: {hashTableState.local_depths[index]}):</strong> {JSON.stringify(bucket)}
          </div>
        ))}
        <div><strong>Directory:</strong> {JSON.stringify(hashTableState.directory)}</div>
      </div>
    );
  };

  return (
    <div className="app-container">
      <div className="App">
        <div className="main-content">
          <h1>{selectedHashing.charAt(0).toUpperCase() + selectedHashing.slice(1)} Hashing Visualization</h1>
          <form onSubmit={handleInsert} className="form-section">
            <h2>Insert</h2>
            <input
              type="number"
              placeholder="Key"
              value={insertKey}
              onChange={(e) => setInsertKey(e.target.value)}
            />
            <button type="submit">Insert</button>
          </form>

          <form onSubmit={handleDelete} className="form-section">
            <h2>Delete</h2>
            <input
              type="number"
              placeholder="Key"
              value={deleteKey}
              onChange={(e) => setDeleteKey(e.target.value)}
            />
            <button type="submit">Delete</button>
          </form>

          <form onSubmit={handleSearch} className="form-section">
            <h2>Search</h2>
            <input
              type="number"
              placeholder="Key"
              value={searchKey}
              onChange={(e) => setSearchKey(e.target.value)}
            />
            <button type="submit">Search</button>
          </form>

          {loading && (
            <div className="loading-indicator">
              <RingLoader size={50} color={'#123abc'} loading={loading} />
            </div>
          )}

          <div className="reset-container">
            <button onClick={handleReset}>Reset</button>
          </div>

          {searchResult && (
            <div className="search-result">
              <h3>Search Result:</h3>
              <p>{searchResult}</p>
            </div>
          )}

          {renderHashTableState()}
        </div>

        {/* Notification Panel */}
        <div className="notification-panel">
          <h2>Notifications</h2>
          {notification.map((note) => (
            <div key={note.id} className={`notification ${note.type}`}>
              <span>{note.message}</span>
              <button onClick={() => setNotification(notification.filter((n) => n.id !== note.id))}>
                ✖
              </button>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default App;
